      method: 'POST',
      headers: {
        Authorization: testToken,
      },
      body: JSON.stringify({
        recruit_id: detailList.id,
      }),
    }).then((res) => res.json());
  };
  console.log(likeuser, '좋아요눌림.');



  useEffect(() => {
    const list = JSON.parse(localStorage.getItem('submitList'));
    setSubmitList(list);
  }, []);

    const { companyId } = useParams();
    return (
        <>
        <AppHeader/>
      <Container>
        <Wrapper>
          <ApplyContents companyId={companyId} />
          {goToApply ? (
            <SubmitTemaplete
              goToApply={goToApply}
              handleMove={toggleApplyBox} // SubmitTemaplete 컴포넌트에서 지원하기 버튼을 누르면 ApplyBox 표시로 변경
              handleValue={handleValue}
              nameValid={nameValid}
              input={input}
              setSubmitList={setSubmitList}
            />
          ) : (
            <ApplyBox
              goToApply={goToApply}
              handleMove={toggleApplyBox}
              recruitList={recruitList} // ApplyBox 컴포넌트에서 지원하기 버튼을 누르면 SubmitTemaplete 표시로 변경
            />
          )}
        </Wrapper>
      </Container>
      </>
    );
  }
  

export default Apply;
