import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';
import ApplyContents from './ApplyContents';
import AppHeader from '../../components/AppHeader';
import ApplyBox from './ApplyBox';
import SubmitTemaplete from './SubmitTemaplete';

const Container = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  flex-direction: column;
  max-width: 1060px;
  margin: 0 auto;
  margin-top: 40px;
`;


const Wrapper = styled.div`
  display: flex;
  width: 1060px;
  position: relative;

  @media (max-width: 992px) {
    width: 100%;
    display: flex;
    flex-direction: column;
  }
`;




function Apply() {

  const [goToApply, setGoToApply] = useState(false);
  const [hideContent, setHideContent] = useState(false);

  const [modal, setModal] = useState(false);
  const [shareModal, setShareModal] = useState(false);

  const [input, setInputs] = useState({
    nameValue: '',
    emailValue: '',
    phoneValue: '',
    recommendValue: '',
  });


  const handleMove = () => {
    setGoToApply(!goToApply);
    setHideContent(!hideContent);
  };

    const { companyId } = useParams();
    return (
        <>
        <AppHeader/>
        <Container>
            <Wrapper>
                <ApplyContents companyId={companyId} />
                <ApplyBox

                  goToApply={goToApply}
                  handleMove={handleMove}
                  handleModal={() => setModal(!modal)}
                  handleShareModal={() => setShareModal(!shareModal)}


                />
                <SubmitTemaplete

                  goToApply={goToApply}
                  input={input}
                  hideContent={hideContent}
                  handleMove={handleMove}

                />
            </Wrapper>
        </Container>
      </>
    );
  }
  

export default Apply;
