import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';
import ApplyContents from './ApplyContents';
import AppHeader from '../../components/AppHeader';
import ApplyBox from './ApplyBox';
import SubmitTemaplete from './SubmitTemaplete';

const Container = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  flex-direction: column;
  max-width: 1060px;
  margin: 0 auto;
  margin-top: 40px;
`;

const Wrapper = styled.div`
  display: flex;
  width: 1060px;
  position: relative;

  @media (max-width: 992px) {
    width: 100%;
    display: flex;
    flex-direction: column;
  }
`;

function Apply() {
  const [goToApply, setGoToApply] = useState(false);

  useEffect(() => {
    console.log('goToApply is now:', goToApply);
  } ,[goToApply]);
  

  const handleMove = () => {
    setGoToApply(true); // 지원하기 버튼 클릭 시 goToApply 상태를 true로 변경
  };

  const { companyId } = useParams();
  
  return (
    <>
      <AppHeader />
      <Container>
        <Wrapper>
        <ApplyContents companyId={companyId} />
          {goToApply ? ( // goToApply 상태에 따라 컴포넌트를 조건부 렌더링
            <SubmitTemaplete
              goToApply={true}
              handleMove={handleMove}
            />
          ) : (
            <ApplyBox
              goToApply={goToApply}
              handleMove={handleMove}
            />
          )}
          
        </Wrapper>
      </Container>
    </>
  );
}

export default Apply;
