import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ApplyTemplateBtn } from './ApplyBox';
import UploadFile from './UploadFile';
import Input_list from './Input_list';

import { useHistory } from 'react-router-dom';

function SubmitTemaplete({
  setSubmitList,
  detailList,
  goToApply,
  input,
  checkSubmit,
  setCheckSubmit,
  handleMove,
  handleValue,
  nameValid,    
  hideContent,
}) {
  const [selectedFile, setSelectedFile] = useState([]);

  const [submit, setSubmit] = useState(false);

  const [currentColor, setCurrentColor] = useState([]);


    // input 객체 초기화
    if (!input) {
        input = {
          nameValue: '',
          emailValue: '',
          phoneValue: '',
          // 다른 필드 초기화
        };
    }

  const inputValid =
    input.nameValue.length &&
    input.emailValue.length &&
    input.phoneValue.length;

  const history = useHistory();

  useEffect(() => {
    currentColor.length ? setSubmit(true) : setSubmit(false);
  }, [currentColor]);

  const HandleOnsubmit = () => {
    if (inputValid && submit) {
      alert('제출이 완료되었습니다.');

      const list = localStorage.getItem('submitList')
        ? JSON.parse(localStorage.getItem('submitList'))
        : '';

      localStorage.setItem(
        'submitList',
        JSON.stringify([
          ...list,
          {
            id: 1,
            companyName: 'Company Name', // 회사 이름을 하드코딩
            position: 'Position', // 포지션을 하드코딩
          },
        ])
      );

      const newList = JSON.parse(localStorage.getItem('submitList'));
      setSubmitList(newList);
      history.push('/');
    }
  };

  const onFileChange = (e) => {
    setSelectedFile([...selectedFile, e.target.files[0].name]);
  };

  const handleCheckValue = (index) => {
    currentColor.includes(index)
      ? setCurrentColor(currentColor.filter((color) => color !== index))
      : setCurrentColor([...currentColor, index]);
  };

  return (
    <>
      <SubmitTemapleteBlock
        goToApply={goToApply}
        input={input}
        checkSubmit={checkSubmit}
        setCheckSubmit={setCheckSubmit}
        hideContent={hideContent}
        nameValid={nameValid}
        selectedFile={selectedFile}
      >
        <SubmitHead>
          <h2>지원하기</h2>
          <button onClick={handleMove}>뒤로</button>
        </SubmitHead>
        <SubmitConstents overflow="auto" height="450px">
          <SubmitText>
            <h2>지원하기</h2>
          </SubmitText>
          {INPUT_LIST.map((list, index) => {
            return (
              <SubmitInputForm nameValid key={index}>
                <h2>{list.name}</h2>
                <input
                  type="text"
                  onChange={handleValue}
                  value={input[list.value]}
                  name={list.value}
                  autoComplete="off"
                  className={`${nameValid && 'red'}`}
                />
              </SubmitInputForm>
            );
          })}
          <SubmitText margin="35px -20px 20px">
            <h2>첨부파일</h2>
          </SubmitText>
          {selectedFile?.map((file, index) => (
            <UploadFile
              index={index}
              currentColor={currentColor}
              setCurrentColor={setCurrentColor}
              selectedFile={file}
              setSubmit={setSubmit}
              submit={submit}
              handleCheckValue={handleCheckValue}
            />
          ))}
          <ApplyTemplateBtn color="#666">
            <form method="post" encType="multipart/form-data">
              <label for="ex_file">파일 업로드</label>
              <input type="file" id="ex_file" onChange={onFileChange} />
            </form>
          </ApplyTemplateBtn>
          <ApplyTemplateBtn color="#666">새 이력서 작성</ApplyTemplateBtn>
          <SubmitConstents fontSize="13px" color="#666">
            큐티드 이력서로 지원하면 최종 합격률이 40% 높아집니다.
          </SubmitConstents>
        </SubmitConstents>
        <SubmitFooter>
          <ApplyTemplateBtn
            color={submit ? 'white' : '#ccc'}
            backgroundColor={submit ? '#3366FF' : '#f2f4f7'}
            onClick={HandleOnsubmit}
          >
            제출하기
          </ApplyTemplateBtn>
        </SubmitFooter>
      </SubmitTemapleteBlock>
      {hideContent ? <HideContent hideContent={hideContent} /> : ''}
    </>
  );
}

export default SubmitTemaplete;

const SubmitTemapleteBlock = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  width: 340px;
  height: 634px;
  border: 1px solid #e1e2e3;
  background-color: #fff;
  position: sticky;
  right: 0;
  top: 20px;
  margin-top: 20px;
  display: ${({ goToApply }) => (goToApply ? 'block' : 'none')};

  @media (max-width: 992px) {
    display: ${(props) => (props.hideContent ? 'flex' : '')};
    position: fixed;
    width: 500px;
    height: 700px;
    border-radius: 4px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 111;
  }
`;

const SubmitHead = styled.div`
  position: fixed;
  background: #fff;
  padding: 15px 20px;
  border-bottom: 1px solid #e1e2e3;
  position: relative;
  h2 {
    text-align: center;
    font-size: 16px;
    font-weight: 600;
    color: #333;
    line-height: 22px;
  }
  button {
    position: absolute;
    top: 50%;
    right: 20px;
    transform: translateY(-50%);
    font-size: 16px;
    font-weight: 600;
    color: #999;
    background-color: white;
    outline: none;
    border: 0;
    cursor: pointer;
  }
`;

const SubmitConstents = styled.div`
  padding: 0 20px;
  margin-top: 20px;
  font-weight: 400;
  line-height: 1.62;
  height: ${({ height }) => height};
  overflow: ${({ overflow }) => overflow};
  font-size: ${({ fontSize }) => fontSize};
  color: ${({ color }) => color};
`;

const SubmitText = styled.div`
  border-left: 2px solid #258bf7;
  padding-left: 20px;
  margin: 0 -20px;
  font-size: 16px;
  font-weight: 600;
  line-height: 22px;
  margin: ${({ margin }) => margin};
`;

const SubmitInputForm = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  height: 50px;
  border-bottom: ${({ nameValid }) =>
    nameValid ? '1px solid #e1e2e3;' : '1px solid red'};
  margin-bottom: 5px;
  font-size: 16px;
  h2 {
    width: 80px;
    line-height: 50px;
    font-size: 16px;
    font-weight: 600;
  }
  input {
    width: calc(100% - 80px);
    height: 50px;
    padding: 0;
    border: none;
    font-size: 15px;
    font-weight: 600;
    border-bottom: 1px solid #e1e2e3;
    outline: none;
    &.red {
      border-bottom: 1px solid red;
    }
  }
`;

const SubmitFooter = styled.div`
  padding: 24px 20px;
  border-top: 1px solid #ececec;
  border-bottom: 1px solid #ececec;
  background: #fff;
`;

const HideContent = styled.div`
  position: fixed;
  z-index: -1;
  width: 100%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.2);
  display: none;

  @media (max-width: 992px) {
    display: ${(props) => (props.hideContent ? 'block' : 'none')};
  }
`;
